import React from 'react';





class profile extends React.Component{
constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: ''
        }
    }
    onEmailChange = (event) => {
        this.setState({ email: event.target.value})
    }
    onNameChange = (event) => {
        this.setState({ password: event.target.value})
    }
    onSubmitSignIn = () => {
        fetch('http://localhost:3001/signin', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                email: this.state.signInEmail,
                password: this.state.signInPassword
            })
        })
        .then(response => response.json())
        .then(data => {
            if (data === 'successfully signed in') {
                this.props.onRouteChange('home');
            }
        })  
    }



render(){
return(
<div>
	<article className="br3 ba dark-gray b--black-10 mv4 w-200 w-50-m w-25-l mw6 shadow-5 center">
                <main className="pa4 black-80">
                    <div className="measure tc">
                        <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                            <legend className="f1 fw6 ph0 mh0">Basic Profile</legend>
                             <div className="mt3">
                                <label className="db fw6 lh-copy f6" htmlFor="name">Email</label>
                                <input className="pa2 input-reset ba bg-transparent hover-bg-dark-gray hover-white w-100" 
                                type="text" 
                                name="name" 
                                id="name"
                                    onChange={this.onNameChange}
                                />
                            </div>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
                                <input className="pa2 input-reset ba bg-transparent hover-bg-dark-gray hover-white w-100" 
                                type="email" 
                                name="email-address" 
                                id="email-address"
                                    onChange={this.onEmailChange}
                                />
                            </div>


                        </fieldset>
                        <div className="">
                            <input
                                onClick={this.onSubmitSignIn}
                                className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib"
                                type="submit" value="Save" />
                        </div>

                    </div>
                </main>
            </article>

</div>

	);

}



}

export default profile;
